import * as React from 'react';
import { classNames } from '@shopify/react-utilities/styles';
import Subheading from '../../../Subheading';
import * as styles from '../../Card.scss';
export default function Section({ children, title, subdued, fullWidth }) {
    const headerContent = title ? (<div className={styles.SectionHeader}>
      <Subheading>{title}</Subheading>
    </div>) : null;
    const className = classNames(styles.Section, subdued && styles['Section-subdued'], fullWidth && styles['Section-fullWidth']);
    return (<div className={className}>
      {headerContent}
      {children}
    </div>);
}
