import * as React from 'react';
import UnstyledLink from '../UnstyledLink';
import * as styles from './Link.scss';
export default function Link({ url, children, onClick, external, id }) {
    return url ? (<UnstyledLink onClick={onClick} className={styles.Link} url={url} external={external} id={id}>
      {children}
    </UnstyledLink>) : (<button type="button" onClick={onClick} className={styles.Link} id={id}>
      {children}
    </button>);
}
