import * as React from 'react';
import Avatar from '../../../Avatar';
import MessageIndicator from '../../../MessageIndicator';
import Menu from '../Menu';
import * as styles from './UserMenu.scss';
export default function UserMenu({ name, detail, avatar, initials, actions, message, onToggle, open, }) {
    const showIndicator = Boolean(message);
    const activatorContentMarkup = (<div className={styles.UserMenu}>
      <MessageIndicator active={showIndicator}>
        <Avatar size="small" source={avatar} initials={initials && initials.replace(' ', '')}/>
      </MessageIndicator>
      <span className={styles.Details}>
        <p className={styles.Name} title="altText">
          {name}
        </p>
        <p className={styles.Detail}>{detail}</p>
      </span>
    </div>);
    return (<Menu activatorContent={activatorContentMarkup} open={open} onOpen={onToggle} onClose={onToggle} actions={actions} message={message}/>);
}
